import axios from 'axios';

const apiUrl = 'http://localhost:5000/api/foods';

export const getAllFoods = async () => {
  try {
    const response = await axios.get(apiUrl);
    return response.data;
  } catch (error) {
    console.error('Error fetching foods:', error);
    throw error;
  }
};

export const addFood = async (foodData) => {
  try {
    const response = await axios.post(apiUrl, foodData);
    return response.data;
  } catch (error) {
    console.error('Error adding food:', error);
    throw error;
  }
};

export const updateFood = async (foodId, foodData) => {
  try {
    const response = await axios.put(`${apiUrl}/${foodId}`, foodData);
    return response.data;
  } catch (error) {
    console.error('Error updating food:', error);
    throw error;
  }
};

export const deleteFood = async (foodId) => {
  try {
    const response = await axios.delete(`${apiUrl}/${foodId}`);
    return response.data;
  } catch (error) {
    console.error('Error deleting food:', error);
    throw error;
  }
};
