import React, { useState } from 'react';
import axios from 'axios';

const FoodForm = ({ fetchFoods }) => {
  const [food, setFood] = useState({
    name: '',
    calories: '',
    protein: '',
    fat: '',
    carbs: ''
  });

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFood({ ...food, [name]: value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    await axios.post('http://localhost:5000/api/foods', food);
    fetchFoods();
    setFood({
      name: '',
      calories: '',
      protein: '',
      fat: '',
      carbs: ''
    });
  };

  return (
    <form onSubmit={handleSubmit}>
      <input
        type="text"
        name="name"
        value={food.name}
        onChange={handleChange}
        placeholder="Name"
        required
      />
      <input
        type="number"
        name="calories"
        value={food.calories}
        onChange={handleChange}
        placeholder="Calories"
        required
      />
      <input
        type="number"
        name="protein"
        value={food.protein}
        onChange={handleChange}
        placeholder="Protein"
        required
      />
      <input
        type="number"
        name="fat"
        value={food.fat}
        onChange={handleChange}
        placeholder="Fat"
        required
      />
      <input
        type="number"
        name="carbs"
        value={food.carbs}
        onChange={handleChange}
        placeholder="Carbs"
        required
      />
      <button type="submit">Add Food</button>
    </form>
  );
};

export default FoodForm;
