import React from 'react';
import Country from './components/Country';
import State from './components/State';
import City from './components/City';
const App = () => {
  const [view, setView] = useState('countries');

  return (
    <div className="App">
      <h1>Country State City Management</h1>
      <div>
        <button onClick={() => setView('countries')}>Countries</button>
        <button onClick={() => setView('states')}>States</button>
        <button onClick={() => setView('cities')}>Cities</button>
      </div>
      <div>
        {view === 'countries' && <Country />}
        {view === 'states' && <State />}
        {view === 'cities' && <City />}
      </div>
    </div>
  );
};

export default App;
