import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Route, Routes } from 'react-router-dom';
import axios from 'axios';
import FoodList from './components/FoodList';
import FoodForm from './components/FoodForm';
import LoginPage from './components/LoginPage';
import AddFoodPage from './components/AddFoodPage'; // Import the AddFoodPage component

const App = () => {
  const [foods, setFoods] = useState([]);
  const [userType, setUserType] = useState('');

  useEffect(() => {
    const storedUserType = localStorage.getItem('userType');
    if (storedUserType) {
      setUserType(storedUserType);
    }
  }, []);

  const fetchFoods = async () => {
    const response = await axios.get('http://localhost:5000/api/foods');
    setFoods(response.data);
  };

  const handleLogout = () => {
    localStorage.removeItem('userType');
    setUserType('');
  };

  return (
    <Router>
      <Routes>
        <Route path="/" element={<LoginPage setUserType={setUserType} />} />
        <Route path="/foods" element={
          <div>
            <h1>Food List</h1>
            {userType === 'nutritionist' && <FoodForm fetchFoods={fetchFoods} />}
            <FoodList foods={foods} fetchFoods={fetchFoods} userType={userType} />
            {userType === 'nutritionist' && <button onClick={handleLogout}>Logout</button>}
            <div>
      <h1>Food List</h1>
      <FoodForm fetchFoods={fetchFoods} />
      <FoodList foods={foods} fetchFoods={fetchFoods} />
    </div>
          </div>
        } />
        <Route path="/addfood" element={<AddFoodPage fetchFoods={fetchFoods} />} /> {/* Add route for AddFoodPage */}
      </Routes>
    </Router>
  );
};

export default App;
