import React, { useState, useEffect } from 'react';
import { getStates, addState, updateState, deleteState, getCountries } from '../api';

const State = () => {
  const [states, setStates] = useState([]);
  const [countries, setCountries] = useState([]);
  const [newStateName, setNewStateName] = useState('');
  const [selectedCountryId, setSelectedCountryId] = useState('');
  const [editStateId, setEditStateId] = useState(null);
  const [editStateName, setEditStateName] = useState('');
  const [editStateCountryId, setEditStateCountryId] = useState('');

  useEffect(() => {
    const fetchStates = async () => {
      const stateData = await getStates();
      setStates(stateData);
    };

    const fetchCountries = async () => {
      const countryData = await getCountries();
      setCountries(countryData);
    };

    fetchStates();
    fetchCountries();
  }, []);

  const handleAddState = async () => {
    const newState = await addState(newStateName, selectedCountryId);
    setStates([...states, newState]);
    setNewStateName('');
    setSelectedCountryId('');
  };

  const handleUpdateState = async (id) => {
    const updatedState = await updateState(id, editStateName, editStateCountryId);
    setStates(states.map(state => (state._id === id ? updatedState : state)));
    setEditStateId(null);
    setEditStateName('');
    setEditStateCountryId('');
  };

  const handleDeleteState = async (id) => {
    await deleteState(id);
    setStates(states.filter(state => state._id !== id));
  };

  return (
    <div>
      <h1>States</h1>
      <input
        type="text"
        value={newStateName}
        onChange={(e) => setNewStateName(e.target.value)}
        placeholder="Add new state"
      />
      <select value={selectedCountryId} onChange={(e) => setSelectedCountryId(e.target.value)}>
        <option value="">Select country</option>
        {countries.map(country => (
          <option key={country._id} value={country._id}>{country.name}</option>
        ))}
      </select>
      <button onClick={handleAddState}>Add State</button>
      <ul>
        {states.map(state => (
          <li key={state._id}>
            {editStateId === state._id ? (
              <>
                <input
                  type="text"
                  value={editStateName}
                  onChange={(e) => setEditStateName(e.target.value)}
                />
                <select
                  value={editStateCountryId}
                  onChange={(e) => setEditStateCountryId(e.target.value)}
                >
                  <option value="">Select country</option>
                  {countries.map(country => (
                    <option key={country._id} value={country._id}>{country.name}</option>
                  ))}
                </select>
                <button onClick={() => handleUpdateState(state._id)}>Save</button>
                <button onClick={() => setEditStateId(null)}>Cancel</button>
              </>
            ) : (
              <>
                {state.name}
                <button onClick={() => {
                  setEditStateId(state._id);
                  setEditStateName(state.name);
                  setEditStateCountryId(state.countryId);
                }}>Edit</button>
                <button onClick={() => handleDeleteState(state._id)}>Delete</button>
              </>
            )}
          </li>
        ))}
      </ul>
    </div>
  );
};

export default State;
