import React, { useState, useEffect } from 'react';
import axios from 'axios';

const FoodForm = ({ fetchFoods, foodToUpdate }) => {
  const [name, setName] = useState('');
  const [calories, setCalories] = useState('');
  const [protein, setProtein] = useState('');
  const [fat, setFat] = useState('');
  const [carbs, setCarbs] = useState('');

  useEffect(() => {
    if (foodToUpdate) {
      setName(foodToUpdate.name);
      setCalories(foodToUpdate.calories);
      setProtein(foodToUpdate.protein);
      setFat(foodToUpdate.fat);
      setCarbs(foodToUpdate.carbs);
    }
  }, [foodToUpdate]);

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (foodToUpdate) {
      await axios.put(`http://localhost:5000/api/foods/${foodToUpdate._id}`, {
        name,
        calories,
        protein,
        fat,
        carbs,
      });
    } else {
      await axios.post('http://localhost:5000/api/foods', {
        name,
        calories,
        protein,
        fat,
        carbs,
      });
    }
    fetchFoods();
    resetForm();
  };

  const resetForm = () => {
    setName('');
    setCalories('');
    setProtein('');
    setFat('');
    setCarbs('');
  };

  return (
    <div>
      <h2>{foodToUpdate ? 'Update Food' : 'Add Food'}</h2>
      <form onSubmit={handleSubmit}>
        <input type="text" placeholder="Name" value={name} onChange={(e) => setName(e.target.value)} />
        <input type="number" placeholder="Calories" value={calories} onChange={(e) => setCalories(e.target.value)} />
        <input type="number" placeholder="Protein" value={protein} onChange={(e) => setProtein(e.target.value)} />
        <input type="number" placeholder="Fat" value={fat} onChange={(e) => setFat(e.target.value)} />
        <input type="number" placeholder="Carbs" value={carbs} onChange={(e) => setCarbs(e.target.value)} />
        <button type="submit">{foodToUpdate ? 'Update' : 'Add'}</button>
      </form>
    </div>
  );
};

export default FoodForm;
