// FoodItem.js
import React, { useState } from 'react';
import axios from 'axios';
// FoodItem.js
import './FoodItem.css';


const FoodItem = ({ food, fetchFoods, userType }) => {
  const [isEditing, setIsEditing] = useState(false);
  const [updatedFood, setUpdatedFood] = useState({
    name: food.name,
    calories: food.calories,
    protein: food.protein,
    fat: food.fat,
    carbs: food.carbs,
  });

  const handleDelete = async () => {
    await axios.delete(`http://localhost:5000/api/foods/${food._id}`);
    fetchFoods();
  };

  const handleUpdate = async (e) => {
    e.preventDefault();
    await axios.put(`http://localhost:5000/api/foods/${food._id}`, updatedFood);
    fetchFoods();
    setIsEditing(false);
  };

  const handleChange = (e) => {
    const { name, value } = e.target;
    setUpdatedFood({ ...updatedFood, [name]: value });
  };

  return (
    <div className="food-item-container">
      {isEditing ? (
        <form onSubmit={handleUpdate}>
          <input
            type="text"
            name="name"
            value={updatedFood.name}
            onChange={handleChange}
            required
          />
          <input
            type="number"
            name="calories"
            value={updatedFood.calories}
            onChange={handleChange}
            required
          />
          <input
            type="number"
            name="protein"
            value={updatedFood.protein}
            onChange={handleChange}
            required
          />
          <input
            type="number"
            name="fat"
            value={updatedFood.fat}
            onChange={handleChange}
            required
          />
          <input
            type="number"
            name="carbs"
            value={updatedFood.carbs}
            onChange={handleChange}
            required
          />
          <button type="submit">Update</button>
          <button type="button" onClick={() => setIsEditing(false)}>Cancel</button>
        </form>
      ) : (
        <>
          <h2>{food.name}</h2>
          <p>Calories: {food.calories}</p>
          <p>Protein: {food.protein}g</p>
          <p>Fat: {food.fat}g</p>
          <p>Carbs: {food.carbs}g</p>
          {userType === 'nutritionist' && (
            <>
              <button onClick={() => setIsEditing(true)}>Update</button>
              <button onClick={handleDelete}>Delete</button>
            </>
          )}
        </>
      )}
    </div>
  );
};

export default FoodItem;
