import React, { useState, useEffect } from 'react';
import { getCountries, getStatesByCountry, getCitiesByState } from './api';

const UserPage = () => {
  const [countries, setCountries] = useState([]);
  const [states, setStates] = useState([]);
  const [cities, setCities] = useState([]);

  const [selectedCountry, setSelectedCountry] = useState('');
  const [selectedState, setSelectedState] = useState('');

  useEffect(() => {
    // Fetch countries on component mount
    const fetchCountries = async () => {
      const countries = await getCountries();
      setCountries(countries);
    };

    fetchCountries();
  }, []);

  useEffect(() => {
    if (selectedCountry) {
      // Fetch states when a country is selected
      const fetchStates = async () => {
        const states = await getStatesByCountry(selectedCountry);
        setStates(states);
        setCities([]);
        setSelectedState('');
      };

      fetchStates();
    } else {
      setStates([]);
      setCities([]);
      setSelectedState('');
    }
  }, [selectedCountry]);

  useEffect(() => {
    if (selectedState) {
      // Fetch cities when a state is selected
      const fetchCities = async () => {
        const cities = await getCitiesByState(selectedState);
        setCities(cities);
      };

      fetchCities();
    } else {
      setCities([]);
    }
  }, [selectedState]);

  return (
    <div>
      <h1>User Page</h1>
      <div>
        <label>
          Country:
          <select value={selectedCountry} onChange={(e) => setSelectedCountry(e.target.value)}>
            <option value="">Select Country</option>
            {countries.map((country) => (
              <option key={country._id} value={country._id}>{country.name}</option>
            ))}
          </select>
        </label>
      </div>
      <div>
        <label>
          State:
          <select value={selectedState} onChange={(e) => setSelectedState(e.target.value)} disabled={!selectedCountry}>
            <option value="">Select State</option>
            {states.map((state) => (
              <option key={state._id} value={state._id}>{state.name}</option>
            ))}
          </select>
        </label>
      </div>
      <div>
        <label>
          City:
          <select value={selectedCity} onChange={(e) => setSelectedCity(e.target.value)} disabled={!selectedState}>
            <option value="">Select City</option>
            {cities.map((city) => (
              <option key={city._id} value={city._id}>{city.name}</option>
            ))}
          </select>
        </label>
      </div>
    </div>
  );
};

export default UserPage;
