import React, { useState, useEffect } from 'react';
import { getCities, addCity, updateCity, deleteCity, getStates } from '../api';

const City = () => {
  const [cities, setCities] = useState([]);
  const [states, setStates] = useState([]);
  const [newCityName, setNewCityName] = useState('');
  const [selectedStateId, setSelectedStateId] = useState('');
  const [editCityId, setEditCityId] = useState(null);
  const [editCityName, setEditCityName] = useState('');
  const [editCityStateId, setEditCityStateId] = useState('');

  useEffect(() => {
    const fetchCities = async () => {
      const cityData = await getCities();
      setCities(cityData);
    };

    const fetchStates = async () => {
      const stateData = await getStates();
      setStates(stateData);
    };

    fetchCities();
    fetchStates();
  }, []);

  const handleAddCity = async () => {
    const newCity = await addCity(newCityName, selectedStateId);
    setCities([...cities, newCity]);
    setNewCityName('');
    setSelectedStateId('');
  };

  const handleUpdateCity = async (id) => {
    const updatedCity = await updateCity(id, editCityName, editCityStateId);
    setCities(cities.map(city => (city._id === id ? updatedCity : city)));
    setEditCityId(null);
    setEditCityName('');
    setEditCityStateId('');
  };

  const handleDeleteCity = async (id) => {
    await deleteCity(id);
    setCities(cities.filter(city => city._id !== id));
  };

  return (
    <div>
      <h1>Cities</h1>
      <input
        type="text"
        value={newCityName}
        onChange={(e) => setNewCityName(e.target.value)}
        placeholder="Add new city"
      />
      <select value={selectedStateId} onChange={(e) => setSelectedStateId(e.target.value)}>
        <option value="">Select state</option>
        {states.map(state => (
          <option key={state._id} value={state._id}>{state.name}</option>
        ))}
      </select>
      <button onClick={handleAddCity}>Add City</button>
      <ul>
        {cities.map(city => (
          <li key={city._id}>
            {editCityId === city._id ? (
              <>
                <input
                  type="text"
                  value={editCityName}
                  onChange={(e) => setEditCityName(e.target.value)}
                />
                <select
                  value={editCityStateId}
                  onChange={(e) => setEditCityStateId(e.target.value)}
                >
                  <option value="">Select state</option>
                  {states.map(state => (
                    <option key={state._id} value={state._id}>{state.name}</option>
                  ))}
                </select>
                <button onClick={() => handleUpdateCity(city._id)}>Save</button>
                <button onClick={() => setEditCityId(null)}>Cancel</button>
              </>
            ) : (
              <>
                {city.name}
                <button onClick={() => {
                  setEditCityId(city._id);
                  setEditCityName(city.name);
                  setEditCityStateId(city.stateId);
                }}>Edit</button>
                <button onClick={() => handleDeleteCity(city._id)}>Delete</button>
              </>
            )}
          </li>
        ))}
      </ul>
    </div>
  );
};

export default City;
