import React, { useState, useEffect } from 'react';
import { getAllFoods } from './api';
import FoodList from './components/FoodList';
import FoodForm from './components/FoodForm';

const App = () => {
  const [foods, setFoods] = useState([]);
  const [error, setError] = useState(null);
  const [currentFood, setCurrentFood] = useState(null);

  const fetchFoods = async () => {
    try {
      const data = await getAllFoods();
      setFoods(data);
    } catch (err) {
      setError('Failed to fetch foods. Please try again later.');
      console.error('Error fetching foods:', err);
    }
  };

  useEffect(() => {
    fetchFoods();
  }, []);

  return (
    <div>
      <h1>Food List</h1>
      {error && <p style={{ color: 'red' }}>{error}</p>}
      <FoodForm fetchFoods={fetchFoods} currentFood={currentFood} setCurrentFood={setCurrentFood} />
      <FoodList foods={foods} fetchFoods={fetchFoods} setCurrentFood={setCurrentFood} />
    </div>
  );
};

export default App;
